// App.js

import React from "react";
import Search from "./components/Search";
import initialDetails from "./data/initialDetails";
import { useSelector } from "react-redux";
import { exportData } from "./utils/exportData"; // Import the exportData function
import "../src/App.css";

function App() {
  const counterState = useSelector((state) => state.counter);

  // Function to handle export button click
  const handleExportClick = () => {
    const updatedDetails = initialDetails.map((person) => ({
      ...person,
      likes: counterState.likesMap[person.id], // Update likes count for each person
    }));

    exportData(updatedDetails); // Export the updated data
  };

  console.log("counterState:", counterState);


  return (
    <div className="App">
      <Search details={initialDetails} />
      <button onClick={handleExportClick}>Export Data</button>
    </div>
  );
}



export default App;
