// Card.js

import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { increment, decrement } from "../features/counter/counterSlice";
import "../css/Card.css";

export default function Card({ person }) {
  const likes = useSelector((state) => state.counter.likesMap[person.id]); // Accessing likes count for the specific person
  const dispatch = useDispatch();

  const handleIncrement = () => {
    dispatch(increment({ id: person.id })); // Pass person's ID to the increment action
  };

  const handleDecrement = () => {
    dispatch(decrement({ id: person.id })); // Pass person's ID to the decrement action
  };

  return (
    <div className="p-50">
      <img
        className="mw-750"
        alt={person.name}
        src={process.env.PUBLIC_URL + person.imgPath}
      />
      <div>
        <h2>{person.name}</h2>
        <p>{person.email}</p>
        <p>{person.address}</p>

        <button aria-label="Increment value" onClick={handleIncrement}>
          +
        </button>
        <p>{likes}</p>
        <button aria-label="Decrement value" onClick={handleDecrement}>
          -
        </button>
      </div>
    </div>
  );
}
