import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { increment, decrement } from '../features/counter/counterSlice';
import '../css/Card.css';

export default function Card({person}) {

const count = useSelector((state) => state.counter.value)
const dispatch = useDispatch()
const likes = person.likes;
const [incrementAmount, setIncrementAmount] = useState({likes});

const handleIncrementAmount = () => {
  dispatch(incrementAmount(Number(incrementAmount)))
};

  return (
    <div className='p-50'>
        <img className='mw-750' alt={person.name} src={process.env.PUBLIC_URL + person.imgPath} />
        <div>
            <h2>{person.name}</h2>
            <p>{person.email}</p>
            <p>{person.address}</p>

            <button aria-label='Increment value' onClick={() => dispatch(increment())}>+</button>
              <p>{count}</p>
            <button aria-label='Increment value' onClick={() => dispatch(decrement())}>-</button>

        </div>      
    </div>
  )
}
