import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { increment, decrement } from '../features/counter/counterSlice';
import '../css/Card.css';

export default function Card({ person }) {
  const likes = useSelector((state) => state.counter.value); // Accessing likes count from Redux store
  const dispatch = useDispatch();

  const handleIncrement = () => {
    dispatch(increment());
  };

  const handleDecrement = () => {
    dispatch(decrement());
  };

  return (
    <div className="p-50">
      <img className="mw-750" alt={person.name} src={process.env.PUBLIC_URL + person.imgPath} />
      <div>
        <h2>{person.name}</h2>
        <p>{person.email}</p>
        <p>{person.address}</p>

        <button aria-label="Increment value" onClick={handleIncrement}>
          +
        </button>
        <p>{likes}</p>
        <button aria-label="Decrement value" onClick={handleDecrement}>
          -
        </button>
      </div>
    </div>
  );
}
