import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { increment, decrement } from '../features/counter/counterSlice';
import '../css/Card.css';

export default function Card({person}) {

const count = useSelector((state) => state.counter.value)
const dispatch = useDispatch()
const [likes, setLikes] = useState(0);

const handleIncrement = () => {
  dispatch(increment());
  setLikes((prevLikes) => prevLikes + 1); // Increment likes for this person
};

const handleDecrement = () => {
  dispatch(decrement());
  setLikes((prevLikes) => prevLikes - 1); // Decrement likes for this person
};

  return (
    <div className='p-50'>
        <img className='mw-750' alt={person.name} src={process.env.PUBLIC_URL + person.imgPath} />
        <div>
            <h2>{person.name}</h2>
            <p>{person.email}</p>
            <p>{person.address}</p>

            <button aria-label='Increment value' onClick={handleIncrement}>+</button>
              <p>{person.likes}</p>
            <button aria-label='Increment value' onClick={handleDecrement}>-</button>

        </div>      
    </div>
  )
}
